import React, { useState, useEffect } from "react";
import {
  Container,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
} from "reactstrap";
import axios from "axios";

const Home = () => {
  const [login, setLogin] = useState("");
  const [password, setPassword] = useState("");
  const onChangeHandler = (event) => {
    if (event.name === "login") {
      setLogin(event.value);
    } else {
      setPassword(event.value);
    }
  };
  const Logon = () => {
    axios
      .get("http://localhost:4000/alumno")
      .then((res) => {
        console.log(res.data);
        const alumno = res.data.find(
          (element) => element.login === login && element.password === password
        );
        console.log(alumno);
        if (alumno) {
          localStorage.setItem("alumno", JSON.stringify(alumno));
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    console.log(login);
  }, [login]);
  return (
    <Container>
      <h1>Log In</h1>
      <Form>
        <FormGroup>
          <Label for="exampleEmail">Email</Label>
          <Input
            type="login"
            name="login"
            id="login"
            placeholder="JohnDoe"
            value={login}
            onChange={(e) => onChangeHandler(e.currentTarget)}
          />
        </FormGroup>
        <FormGroup>
          <Label for="examplePassword">Password</Label>
          <Input
            type="password"
            name="password"
            id="Password"
            placeholder="aaaaa"
            value={password}
            onChange={(e) => onChangeHandler(e.currentTarget)}
          />
        </FormGroup>
        <Button onClick={() => Logon()}>Log In</Button>
      </Form>
    </Container>
  );
};
export default Home;
